package com.ustglobal.beanobject.bean;

public class Student {
	private int id;
	private String name;
	private int rollno;

	public Student() {
		// TODO Auto-generated constructor stub
	}
	


	public void setId() {

		this.id=id;


	}

	public int getId() {
		return id;


	}


	public void setName() {

		this.name=name;


	}

	public int getName() {
		return name;
	}

	public void setRollNo(int rollno)
	{

		this.rollno = rollno;

	}


	public int getRollNo() {

		return rollno;
	}



}

